import React, { Component } from 'react';

class Certificate extends Component {
  render() {

    if(this.props.data){
      var projects = this.props.data.projects.map(function(Certificate){
        var projectImage = 'images/portfolio/'+Certificate.image;
        return <div key={Certificate.title} className="columns portfolio-item">
           <div className="item-wrap">
            <a href={Certificate.url} title={Certificate.title}>
               <img alt={Certificate.title} src={projectImage} />
               <div className="overlay">
                  <div className="portfolio-item-meta">
                 <h5>{projects.title}</h5>
                     <p>{projects.category}</p>
                  </div>
                </div>
              <div className="link-icon"><i className="fa fa-link"></i></div>
            </a>
          </div>
        </div>
      })
    }

    return (
      <section id="certificate">

      <div className="row">

         <div className="twelve columns collapsed">

            <h1>Check Out Some of My Certificates.</h1>

            <div id="portfolio-wrapper" className="bgrid-quarters s-bgrid-thirds cf">
                {projects}
            </div>
          </div>
      </div>
   </section>
    );
  }
}

export default Certificate;
